
CREATE TABLE "DB2INST1"."PRODUCT"  (
		  "ID" BIGINT NOT NULL GENERATED BY DEFAULT AS IDENTITY (  
		    START WITH +1  
		    INCREMENT BY +1  
		    MINVALUE +1  
		    MAXVALUE +9223372036854775807  
		    NO CYCLE  
		    CACHE 20  
		    NO ORDER ) , 
		  "DESCRIPTION" VARCHAR(255 OCTETS) , 
		  "IMAGE_URL" VARCHAR(255 OCTETS) , 
		  "PRICE" DECIMAL(19,2) )   
		 IN "USERSPACE1"  
		 ORGANIZE BY ROW  ;
ALTER TABLE "DB2INST1"."PRODUCT" 
	ADD PRIMARY KEY
		("ID")
	ENFORCED ;
ALTER TABLE "DB2INST1"."PRODUCT" ALTER COLUMN "ID" RESTART WITH 21 ;

